mapper.select:
select * from ( select rownum RNUM, zxcvb.* from ( select d.report_no||'' as "report_no" ,(select emp_name from employee where emp_no=d.emp_no) as "emp_name" ,(select busi_type_name from code_business_type where busi_type_code = d.busi_type_code ) as "busi_type_name" ,
case when length(busi_content) >= 10 then substr(busi_content,0,10) || '...' else busi_content end as "busi_content"
,revise_cnt||'' as "revise_cnt" ,to_char(busi_start_date,'YYYY-MM-DD HH24:MI DY','NLS_DATE_LANGUAGE = KOREAN') as "busi_start_date" ,to_char(busi_end_date,'YYYY-MM-DD HH24:MI DY','NLS_DATE_LANGUAGE = KOREAN') as "busi_end_date" ,d.emp_no as "emp_no" ,report_code||'' as "report_code" from daily_report d where 1=1
and ( d.emp_no = #{emp_no} or ( d.report_code =1 and d.report_no in(select report_no from sign_finish_report where emp_no=#{emp_no} and report_code=1) ) )
order by d.report_no desc
) zxcvb) where RNUM >=#{begin_rowNo} and RNUM <=#{end_rowNo}

mapper.insert:
insert into daily_report(
	report_no
	,report_code
	,busi_type_code
	,busi_start_date
	,busi_end_date
	,busi_content
	,emp_no
	,revise_cnt
)values(
	(select nvl(max(report_no),0)+1 from daily_report)
	,#{report_code}
	,#{busi_type_code}
	,to_date(#{busi_start_date},'YYYY-MM-DD-HH24:MI')
	,to_date(#{busi_end_date},'YYYY-MM-DD-HH24:MI')
	,#{busi_content}
	,#{emp_no}
	,0
)

insert into return_report(
	sfr_no
	,return_result
)values(
	(select (sign_finish_no) from sign_finish_report where report_no=#{report_no} and emp_no=#{emp_no} and report_code='1')
	,#{return_result}
)

mapper.update:
update
	daily_report
set
	busi_content = #{busi_content}
	,busi_type_code = #{busi_type_code}
	,busi_start_date = to_date(#{busi_start_date},'YYYY-MM-DD-HH24:MI')
	,busi_end_date =  to_date(#{busi_end_date},'YYYY-MM-DD-HH24:MI')
where
	emp_no = #{emp_no}
	and
	report_no = #{report_no}
	and
	report_code = '1'
