mapper.insert:
	    insert into expense_report(
            report_no
            ,report_code
            ,report_name
            ,expense_field_code
            ,expense_price
            ,expense_method_code
            ,expense_date
            ,receipt_no
            ,remarks
            ,emp_no
        )values(
            (select nvl(max(report_no),0)+1 from expense_report)
            ,3
            ,#{report_name} 
            ,#{expense_field_code}  
            ,#{expense_price} 
            ,#{expense_method_code} 
            ,to_date(#{expense_date},'YYYY-MM-DD')
            ,#{receipt_no}
            ,#{remarks}
            ,#{emp_no}
        )

mapper.update:
		update
			expense_report
		set
			expense_price = #{expense_price}
            ,expense_field_code = #{expense_field_code} 
            ,expense_method_code = #{expense_method_code}
            ,expense_date = to_date(#{expense_date},'YYYY-MM-DD')
		where
			emp_no = #{emp_no}
			and
			report_no = #{report_no}
            and
            report_code = 3

mapper.insert:
insert into return_report(
	sfr_no
	,return_result
)values(
	(select (sign_finish_no) from sign_finish_report where report_no=#{report_no} and emp_no=#{emp_no} and report_code=3 )
	,#{return_result}
)

mapper.update:
		update
			expense_report
		set
			receipt_no = #{receipt_no}
		where
			emp_no = #{emp_no}
			and
			report_no = #{report_no} 
            and
            report_code = 3

		update
			expense_report
		set
			remarks = #{remarks}
		where
			emp_no = #{emp_no}
			and
			report_no = #{report_no}
            and
            report_code = 3

mapper.select:
select * from ( select rownum RNUM, zxcvb.* from ( select report_no||'' as "report_no" ,(select emp_name from employee where emp_no=e.emp_no) as "emp_name" , case when length(report_name) >= 10 then substr(report_name,0,10) || '...' else report_name end as "report_name" ,expense_field_code as "expense_field_code" ,(select expense_field_name from code_expense_field where expense_field_code = e.expense_field_code) as "expense_field_name" ,to_char(expense_date,'YYYY-MM-DD','NLS_DATE_LANGUAGE = KOREAN') as "expense_date" ,REGEXP_REPLACE(REVERSE(REGEXP_REPLACE(REVERSE(TO_CHAR(expense_price)), '(\d{3})','\1,')), '^,','') as "expense_price" ,expense_method_code as "expense_method_code" ,(select expense_method_name from code_expense_method where expense_method_code = e.expense_method_code) as "expense_method_name" ,report_code||'' as "report_code" ,emp_no||'' as "emp_no" from expense_report e where 1=1
and ( e.emp_no = #{emp_no} or ( e.report_code =3 and e.report_no in(select report_no from sign_finish_report where emp_no=#{emp_no}and report_code=3) ) )
order by e.report_no desc ) zxcvb) where RNUM >=#{begin_rowNo} and RNUM <=#{end_rowNo}
