insert into employee
(
		emp_no
		,emp_name
		,emp_pwd
		,jikup_code
		,email
		,mgr_emp_no
		,dep_no
		,jumin_num
		,phone_num
		,allow_code 
		,emp_role     		
)
values
(
		(select nvl(max(emp_no),0)+1 from employee where emp_no  <  9999 )    
		,#{uname}
		,#{pwd}
		,(select jikup_code from code_jikup where jikup_code='6')
		,#{email}



		 	||#{domain2}


		    ||#{domain}



		,0
		,#{affiliation_dept}
		,#{front_jumin_num}||#{back_jumin_num}
		,#{tel_num1}||#{tel_num2}||#{tel_num3}
		,2 
		,'user'
)
