mapper.insert:
insert into board(
	b_no
	,subject			
	,readcount
	,content

)
values
(
	(select nvl(max(b_no),0)+1 from board)
	,#{subject}			
	,0
	,#{content}

)

mapper.update:
update
	board
set
	subject = #{subject}

	,content = #{content}

where
	b_no=#{b_no}
