mapper.insert:
        insert into sales_report(
            report_no
            ,report_code
            ,report_name
            ,sales_type_code
            ,sales_date_start
            ,sales_date_end
            ,sales_content
            ,sales_result
            ,emp_no
            ,revise_cnt
            ,sales_company
            ,sales_name
            ,sales_jikup
            ,sales_phone
        )values(
            (select nvl(max(report_no),0)+1 from sales_report)
            ,2
            ,#{report_name}
            ,${sales_type_code}
            ,to_date(#{sales_date_start},'YYYY-MM-DD-HH24:MI')
            ,to_date(#{sales_date_end},'YYYY-MM-DD-HH24:MI')
            ,#{sales_content}
            ,#{sales_result}
            ,${emp_no}
            ,0 
            ,#{sales_company} 
            ,#{sales_name}
            ,#{sales_jikup}
            ,#{sales_phone}
		)

mapper.update:
		update
			sales_report
		set
			sales_result = #{sales_result}
			,sales_company = #{sales_company} 
			,sales_name = #{sales_name}
            ,sales_jikup = #{sales_jikup}
            ,sales_phone = #{sales_phone}
            ,sales_date_start = to_date(#{sales_date_start},'YYYY-MM-DD-HH24:MI')
            ,sales_date_end = to_date(#{sales_date_end},'YYYY-MM-DD-HH24:MI')
            ,sales_type_code = ${sales_type_code}
		where
			emp_no = #{emp_no}
			and
			report_no = #{report_no}
			and
			report_code = 2

mapper.insert:
insert into return_report(
	sfr_no
	,return_result
)values(
	(select (sign_finish_no) from sign_finish_report where report_no=#{report_no} and emp_no=#{emp_no} and report_code=2 )
	,#{return_result}
)

mapper.update:
update
	sales_report
set
	sales_content = #{sales_content}
where
	emp_no = #{emp_no}
	and
	report_no = #{report_no}
	and
	report_code = 2

mapper.select:
select * from ( select rownum RNUM, zxcvb.* from ( select report_no||'' as "report_no" ,(select emp_name from employee where emp_no=s.emp_no) as "emp_name" ,report_name as "report_name" ,(select sales_type_name from code_sales_type where sales_type_code=s.sales_type_code) as "sales_type_name" ,to_char(sales_date_start,'YYYY-MM-DD HH24:MI DY','NLS_DATE_LANGUAGE = KOREAN') as "sales_date_start" ,to_char(sales_date_end,'YYYY-MM-DD HH24:MI DY','NLS_DATE_LANGUAGE = KOREAN') as "sales_date_end" ,emp_no as "emp_no" ,report_code||'' as "report_code" ,revise_cnt||'' as "revise_cnt" from sales_report s where 1=1
and ( s.emp_no = ${emp_no} or s.report_code in(select report_code from sign_finish_report where emp_no=${emp_no}) and s.report_no in(select report_no from sign_finish_report where emp_no=${emp_no}and report_code=2) )
order by s.report_no desc
) zxcvb) where RNUM >=#{begin_rowNo} and RNUM <=#{end_rowNo}
